page.script:

    	function hide_class(dropdownID) {

			var selval = jQuery("#" + dropdownID).val();

			jQuery("input." + dropdownID).each( function (index, field)
			{
		    	if(field.name == 'hide_' + selval){
		    		jQuery('.' + field.value).hide();
		  		}
		  		if(field.name == 'show_' + selval) {
		  			jQuery('.' + field.value).show();
		  		} else {
		  			jQuery('.' + field.value).hide();
		  		}
			});
		}

(function(d, s, id) {
		  var js, fjs = d.getElementsByTagName(s)[0];
		  if (d.getElementById(id)) return;
		  js = d.createElement(s); js.id = id;
		  js.src = "//connect.facebook.net/en_GB/all.js#xfbml=1";
		  fjs.parentNode.insertBefore(js, fjs);
		}(document, 'script', 'facebook-jssdk'));

		jQuery.noConflict();
		jQuery(document).ready(function(){			
			jQuery('textarea').elastic();
			jQuery('textarea').trigger('update').linedtextarea({selectedLine: 1}).elastic();
		});

	if (typeof(Zenbox) !== "undefined") {
		Zenbox.init({
			dropboxID:   "20040053",
			url:         "https://themekraft.zendesk.com",
			tabID:       "questions",
			tabColor:    "black",
			tabPosition: "Left",
			hide_tab: true
  		});
	}

